--ACCION
/*
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES (1, N'1.1.1', N'Potenciar la elaboración de evaluaciones sectoriales de riesgo LA/FT de los sujetos obligados identificados por las recomendaciones del GAFI.', N'"La UIF ha elaborado 1 Evaluación Sectorial de la Exposición a los Riesgos de LA/FT del Sistema Financiero. El DSRLAFT, mediante Informe N° 78-2018-DSRLAFT, ha presentado los resultados de la evaluación sectorial de la exposición de riesgos LA/FT de las empresas del Sistema Bancario, Financiero y AFP. Pendiente elaborar durante el 2018: una evaluación en el sector de Juegos de Casino y Máquinas Tragamonedas a cargo del MINCETUR. "', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES (1, N'1.2.1', N'Impulsar la reforma normativa ante el Congreso de la República para que la SBS supervise prudencialmente a las cooperativas de ahorro y crédito que no capten recursos del público. ', N'Aprobación de la Ley N° 30822, Ley que modifica la Ley N° 26702, Ley General del Sistema Financiero y del Sistema de Seguros y Orgánica de la SBS, y otras normas concordantes, respecto de la regulación y supervisión de las cooperativas de ahorro y crédito. Se dispuso la creación de la Superintendencia Adjunta de Cooperativas encargada de la supervisión de las Coopac de niveles 1 y 2. En materia de prevención de LA, el supervisor en el caso de las nivel 1 es la UIF-Perú, y en las de niveles 2 y 3 estará a cargo de la SBS. Se ha desarrollado un régimen de supervisión, intervención, disolución y liquidación, infracciones y sanciones. Además, se dispuso la creación de un Fondo de Seguro de Depósito Cooperativo y un Registro Nacional de Cooperativas de Ahorro y Crédito no autorizadas a captar recursos.', 1, N'Cumplido', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES (1, N'1.2.2', N'Proponer e impulsar la normativa ante el Congreso de la República y/o el Poder Ejecutivo (facultades delegadas) para regular las actividades de compra y venta de divisas, a fin de establecer requisitos específicos para el inicio (licenciamiento) y realización de estas actividades.', N'Se han realizado reuniones de trabajo y coordinaciones entre la SAAJ, UIF-Perú y DAR. Se ha elaborado un único proyecto de norma que comprende la regulación de las actividades de compra y venta de divisas, así como las de préstamos, que se encuentra en proceso de revisión para presentarlo e impulsar su aprobación ante el Congreso de la República. ', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES (1, N'1.2.3', N'Proponer e impulsar la normativa ante el Congreso de la República y/o el Poder Ejecutivo (facultades delegadas) para regular las actividades de préstamo, a fin de establecer requisitos específicos para el inicio (licenciamiento) y realización de estas actividades.', N'Se han realizado reuniones de trabajo y coordinaciones entre la SAAJ, UIF-Perú y DAR. Se ha elaborado un único proyecto de norma que comprende la regulación de las actividades de compra y venta de divisas, así como las de préstamos, que se encuentra en proceso de revisión para presentarlo e impulsar su aprobación ante el Congreso de la República. ', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES (1, N'1.2.4', N'Aprobar el desarrollo normativo, de acuerdo con lo establecido en los estándares internacionales, para el cumplimiento de las obligaciones de los/as abogados/as y contadores/as en su condición de sujetos obligados.', N'"Art. 4 de la Resolución SBS N° 789-2018 “Norma para la prevención del lavado de activos y del financiamiento del terrorismo aplicable a los sujetos obligados bajo supervisión de la UIF-Perú, en materia de prevención del lavado de activos y del Financiamiento del Terrorismo” señala lo siguiente: Los sujetos obligados abogados y contadores públicos colegiados deben implementar un sistema de prevención del lavado de activos y financiamiento del terrorismo (SPLAFT). Por ello deben cumplir con: a)   El proceso de debida diligencia en el conocimiento del cliente: deben identificar al cliente en cada operación, sea este persona natural, persona jurídica o ente jurídico, solicitando la información y documentación determinada según se trate del régimen general o reforzado que deben seguir.  b)   Bajo el régimen general de debida diligencia el cliente: los abogados y contadores a que se refiere este artículo deben solicitar a sus clientes la información mínima. c)  Bajo el régimen reforzado, los sujetos obligados deben desarrollar e implementar procedimientos adicionales a los del régimen general, para los clientes que demuestren un patrón de mayor riesgo del LA/FT. Este régimen aplica obligatoriamente a clientes: 1) Nacionales o extranjeros, no residentes. 2) Personas jurídicas no domiciliadas. 3) PEP. d) Para efectos del Registro de Operaciones (RO), los sujetos obligados abogados y contadores deben registrar todas las operaciones individuales siguientes, independientemente del monto de la operación e)   El oficial de cumplimiento de los sujetos obligados abogados y contadores debe enviar a la UIF-Perú Informe Anual del Oficial de Cumplimiento (IAOC) sobre la situación del SPLAFT y su cumplimiento."', 1, N'Cumplido', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES (1, N'1.2.5', N'Aprobar la normativa que establezca la determinación y monitoreo de los requisitos de idoneidad aplicables a los/as accionistas, directores/as, gerentes/as y principales funcionarios/as organizadores/as de los sujetos obligados supervisados integralmente por la SBS.', N'Art. 22 del D.S. N° 020-2017-JUS, Reglamento de la Ley N° 27693, Ley que crea la UIF-Perú, dispone que los sujetos obligados deben implementar una política de debida diligencia en el conocimiento de sus trabajadores (que incluye al gerente general, gerentes, administradores) y directores, de contar con dicho órgano de gobierno, la que debe formar parte de su programa o política de reclutamiento y selección del personal, con el fin de garantizar su integridad, para lo cual deben identificarlos y requerirles información, considerando lo establecido en las normas sectoriales. Asimismo, en el artículo 17 de la Resolución SBS N° 789-2018 "Norma para la prevención del LA y FT aplicable a los sujetos obligados bajo supervisión de la UIF-Perú, en materia de prevención del LA y del FT", señala la información mínima que se le debe requerir a los trabajadores y directores. Por otro lado, la propuesta de idoneidad moral de organizadors y accionistas fue incorporada en el nuevo Reglamento de Autorización de Empresas y Representantes de los Sistemas Financieros y de Seguros, así como la modificación del Título II del Compendio de Normas de Superintendencia Reglamentarias del Sistema Privado de AFP, aprobado por la Resolución N° 054-98-EF/SAFP y la modificación del Reglamento de Gobierno Corporativo y Gestión Integral de Riesgos.', 1, N'Cumplido', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES (1, N'1.2.6', N'Modificar el Reglamento de Sanciones en materia LA/FT para los sujetos obligados supervisados integralmente por la SBS, para que se establezcan sanciones eficaces, proporcionales y disuasivas.', N'Aprobación de la Resolución SBS N° 2755-2018 se aprobó el "Reglamento de infracciones y sanciones de la Superintendencia de Banca, Seguros y Administradoras Privadas de Fondos de Pensiones". ', 1, N'Cumplido', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES (1, N'1.2.7', N'Modificar y/o actualizar, cuando corresponda, el Reglamento de Sanciones de la SMV en materia LA/FT.', N'', 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES (1, N'1.2.8', N'Aprobar una normativa que, dentro del marco de la Leyes 27693 y N° 26702, regule el régimen sancionador para todos los sujetos obligados supervisados por la UIF-Perú y demás supervisores, excepto la SBS y la SMV.', N'Se encuentra en proceso de revisión la actualización del Reglamento de Infracciones y Sanciones aplicable a los sujetos obligados bajo supervisión de la UIF-Perú aprobado por Resolución SBS N° 8930-2012 y sus normas modificatorias, acorde con las nuevas disposiciones establecidas en el TUO e la Ley de Procedimiento Administrativo General. Asimismo, se han realizado coordinaciones con la SUNAT y el MTC para elaborar los proyectos de Reglamentos de Infracciones y Sanciones en materia de prevención LA/FT aplicable a sus supervisados, los cuales se encuentran en proceso de revisión.', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'1.2.9', N'Proponer e impulsar la normativa ante el Congreso de la República y/o el Poder Ejecutivo (facultades delegadas) para fortalecer el régimen sancionador que aplica la SBS. ', N'Elaboración de un proyecto de ley que propone la modificación del art. 361 de la Ley N° 26702, Ley General del Sistema Financiero y Sistema de Seguros, que establece como tope máximo para las multas 200 UITs. Para ello, se ha dispusto la contratación de una consultoría financiada por GIZ encargada de analizar, entro otros aspéctos, la regulación y los estándares internacionales aplicados por otros supervisores para la determinación de multas, lo cual coadyuvará en la elaboración del proyecto de ley respectivo. ', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'1.2.10', N' Aprobar la reforma normativa referida a los requisitos de licenciamiento para el inicio y realización de actividades de servicio de remesa postal supervisados por el MTC.', N'', 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'1.2.11', N'Aprobar y/o actualizar la reforma normativa en base a los resultados de las evaluaciones sectoriales de riesgo en materia LA/FT, cuando estos lo recomienden.', N'', 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'1.2.12', N'Proponer e impulsar la reforma normativa ante el Congreso de la República   y/o el Poder Ejecutivo (facultades delegadas) para acceder de forma oportuna a la información actualizada sobre los beneficiarios finales de las personas y/o entes jurídicos.', N'Aprobación del Decreto Legislativo N° 1372, Decreto Legislativo que regula la obligación de las Personas Jurídicas y/o entes jurídicos de informar la identificación de los beneficiarios finales.   Se establece la obligación de las PPJJ y/o entes jurídicos de informar a través de una DJ, presentada a la SUNAT, la identificación de los beneficiarios finales. Además, otorga a las autoridades competentes acceso a información precisa y actualizada sobre el beneficiario final. Y se aprobó el Decreto Supremo N° 003-2019-EF, que aprueba el Reglamento del Decreto Legislativo N° 1372, que regula la obligación de las personas jurídicas y/o entes jurídicos de informar la identificación de los beneficiarios finales.', 1, N'Cumplido', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'1.3.1', N'Potenciar la planificación de la supervisión anual en materia LA/FT con un enfoque basado en riesgos.', N'Se ha presentado el Plan Anual de Visitas del DSRLAFT del año 2018. El Plan Anual de visitas 2019 del DSRLAFT ha sido culminado durante el mes de octubre del 2018, encontrándose actualmente en etapa de consolidación e integración en la SAR.  Por otro lado, UIF-Perú ha elaborado el Plan de supervisión anual en materia de LA/FT con un enfoque basado en riesgos. ', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'1.3.2', N'Definir y formalizar los canales de comunicación entre los organismos supervisores y los sujetos obligados, dirigidos a retroalimentarse de información para mejorar el grado de cumplimiento del marco preventivo en materia LA/FT.', N'SAR: Lanzamiento de la nueva plataforma del aplicativo del Informe Semestral del Oficial de Cumplimiento (ISOC), se sustivieron 5 reuniones con la totalidad de empresas supervisadas por la SBS para presentar el nuevo aplicativo y recibir sus comentarios antes del lanzamiento.                                                                                                 UIF-Perú: 56 sujetos obligados recibieron retroalimentaciones a cargo de D.S. Publiación del documento normativo interno SBS-PRO-UIF526-01, que incluye los lineamientos de retroalimentación del RO a los sujetos obligados según lo reportado por el DAE: 110 retroalimentaciones en materia RO a diversos sujetos obligados. El DAO realizó 365 retroalimentaciones indivisualizadas a un total de 23 tipos de SO, y se realizaron 91 retoralimentaciones grupales a un total de 30 tipos de SO.', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'1.3.3', N'Capacitar a los sujetos obligados en materia LA/FT dirigidas a mejorar su grado de cumplimiento, con especial incidencia en los de mayor riesgo.', N'', 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'1.4.1', N'Identificar los sectores con bajos, nulos o con deficiente calidad de reporte a la UIF-Perú, a fin de orientar los planes de supervisión, cuando corresponda.', N'Mediante el Informe N° 013-2018-DAE-UIF-SBS, se identificaron los sectores que presentan bajos o nulos niveles de reporte de operaciones sospechosas, así como, aquellos con deficiente calidad de reporte; con el fin de orientar los planes de supervisión y recomendar las medidas necesarias para recibir ROS de calidad.', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'1.4.2', N'Aprobar un nuevo formulario que facilite la comunicación de operaciones sospechosas por los sujetos obligados, que incluya un instructivo para su adecuada comprensión y aplicación.', N'Resolución SBS N° 5085-2018 que aprueba 02 “Retroalimentación de incidencias en información enviada por los sujetos obligados". . Asimismo, se adjuntó el instructivo de llenado y los manuales de usuario. Cabe precisar, que el instructivo, los manales y un tutorial se encuentran publicados en el Portal PLAFT desde mayo 2018. Además, se ha brindado capacitación a 286 SO.', 1, N'Cumplido', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'1.4.3', N'Proponer e impulsar ante el Ministerio de Justicia y Derechos Humanos modificaciones normativas que permitan la adecuación de los plazos de comunicación de operaciones sospechosas vinculadas con FT.', N'Artículo 25 del Decreto Supremo N°020-2017-JUS, sobre el Reporte de Operaciones Sospechosas, establece que los sujetos obligados tienen la obligación de comunicar a la UIF-Perú a través de su oficial de cumplimiento, las operaciones detectadas en el curso de sus actividades, realizadas o que se hayan intentado realizar, que sean consideradas como sospechosas, sin importar los montos involucrados. La comunicación debe ser realizada de forma inmediata y suficiente, es decir, en un plazo que en ningún caso debe exceder las veinticuatro horas (24) desde que la operación es calificada como sospechosa.', 1, N'Cumplido', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'1.4.4', N'Proporcionar de forma permanente y periódica información a los sujetos obligados sobre las tipologías y tendencias de LA/FT que les puedan afectar.', N'Se publicó en la página web de la SBS las tipologías LA/FT identificadas durante el presente año, en temas relacionados a modalidades de financiamiento de terrorismo, miembros de una organización sin fines de lucro OSFL que realiza actividades de financiamiento del terrorismo, mal uso de los recursos de una OSFL vinculada a una persona políticamente expuesta y tala ilegal. ', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'2.1.1.', N'Potenciar y desarrollar los mecanismos que permitan hacer operativo el acceso de la UIF-Perú a la información protegida por el secreto bancario y la reserva tributaria.', N'"1. Resolución SBS N° 4353-2017, la cual regula la forma y condiciones en que se debe proporcionar a la UIF-Perú la información protegida por el secreto bancario y/o la reserva tributaria. 2. Resolución SBS N° 4589-2017 que establece como comunicaciones de inteligencia financiera nacional, de carácter confidencial y reservado emitidas por la UIF-Perú, al Informe de Inteligencia para Levantamiento de Reservas y a la Nota de Inteligencia Financiera Espontánea.  3. Resolución Administrativa N°090-2017-CE-PJ dispone que los Juzgados de Investigación Preparatoria y Salas de Apelación que conforman la Sala Penal Nacional (LA proveniente de los todos los delitos, excepto corrupción; y, FT) y el Sistema Especializado en Delitos de Corrupción de Funcionarios (LA proveniente de corrupción) conozcan las solicitudes de la UIF sobre levantamiento del secreto bancario y reserva tributaria."', 1, N'Cumplido', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'2.1.2.', N'Proponer e impulsar la reforma normativa ante el Congreso de la República para lograr que la UIF-Perú comparta información de inteligencia financiera con el Sistema de Inteligencia Nacional (SINA), el Jurado Nacional de Elecciones (JNE) y la Oficina Nacional de Procesos Electorales (ONPE).', N'El Departamento de Regulación de la SBS está evaluando los procedimientos, condiciones, tipo de información y demás aspectos necesarios que permitan establecer un adecuado y oportuno intercambio de información entre la UIF-Perú y el SINA, JNE y el ONPE, dentro del marco de la Ley N° 27693 y sus normas reglamentarias.', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'2.2.1.', N'Elaborar un diagnóstico de zonas vulnerables de frontera, desde el punto de vista de flujos de activos ilícitos, o con fines ilícitos.', N'', 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'2.2.2.', N'Elaborar y ejecutar un Plan de control conjunto entre entidades nacionales en puertos, aeropuertos, centros de atención en frontera y zonas de frontera en función del resultado del diagnóstico, contemplando su coordinación con países que se estimen relevantes.', NULL, 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'2.2.3.', N'Incrementar el número de representantes del MP en zonas de frontera, en las áreas geográficas de mayor riesgo identificadas por el diagnóstico', NULL, 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'2.2.4', N'Elaborar e impulsar propuestas normativas ante el Congreso de la República que permitan establecer límites al traslado interno de movimiento de efectivo y valores y fomentar la bancarización. ', N'"Aprobación de la Ley N° 30730, publicada el 21 de febrero de 2018, que establece la bancarización obligatoria de determinadas operaciones pasibles de registros, siempre que superen determinado umbral. La obligatoriedad de utilizar medios de pago para realizar las siguientes operaciones cuando los montos sean iguales o superiores a 3 UIT: i) constitución o transferencia de derechos reales sobre inmuebles, ii) transferencia de propiedad o constitución de derechos reales sobre vehículos nuevos o usados, ya sean aéreos, marítimos o terrestres; y iii) adquisición, aumento y reducción de participación en el capital social de una persona jurídica. Consultoría en proceso de elaboración de propuesta normativa que permita establecer límites al traslado interno de movimiento de efectivo y valores con asistencia técnica de GIZ. Se continúa realizando investigaciones y estudiando la propuesta de esta medida. "', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'2.2.5', N'Promover y/o adoptar medidas normativas que permitan desincentivar el uso de dinero en efectivo, dificultando su utilización con fines LA/FT.  ', N'"Aprobación de la Resolución SBS N°4705-2017 que incorpora el artículo 50-A en la Resolución SBS N°2660-2015, el mismo que señala lo siguiente: Las empresas deben solicitar información adicional a la señalada en el artículo 50, que permita determinar y sustentar el origen de fondos cuando se efectúen operaciones en moneda extranjera en efectivo, de acuerdo a lo considerado en el artículo 49 del Reglamento, por importes iguales o superiores a: a) Tratándose de transferencias de fondos, el importe es de siete mil quinientos dólares americanos (US$ 7,500.00), su equivalente en otras monedas extranjeras, de ser el caso. b) Cuando se trate de operaciones de compra y/o venta de divisas, el importe es de diez mil dólares americanos (US$ 10,000.00), su equivalente en otras monedas extranjeras, de ser el caso.  c) Para las operaciones no consideradas en los literales precedentes, el importe a considerar es de cincuenta mil dólares americanos (US$ 50,000.00), su equivalente en otras monedas extranjeras, de ser el caso.   Las empresas deben incorporar en el Manual u otro documento normativo interno el listado de información de sustento que solicitarán para este tipo de operaciones en efectivo, así como las medidas que adoptarán cuando el ejecutante de la operación se niegue a proporcionar la información solicitada. Las empresas pueden establecer umbrales menores, de acuerdo a la identificación y evaluación de riesgos que hayan efectuado de acuerdo a lo señalado en el artículo 25 del Reglamento."', 1, N'Cumplido', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'2.2.6', N'Repotenciar a SUNAT-ADUANAS y DICAPI de los medios técnicos y tecnológicos necesarios para realizar con efectividad las intervenciones en puertos, aeropuertos y centros de atención en frontera y zonas de frontera.', N'Durante el 2018, la SUNAT ha reportado que se ha realizado el equipamiento tecnológico (equipos de detector de trazas y explosivos, identificador de químicos, kit anticontrabando, bodyscan, etc.) a nivel nacional. ', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'2.3.1', N'Elaborar protocolos y canales de comunicación segura para la remisión de información confidencial de la UIF-Perú al MP y a otras autoridades competentes.', N'Se eleaboró un protocolo de uso del SCES (Sistema de Comunicación con entidades Solicitantes). Se ha implementado el acceso para realizar pedidos a través del SCES al Ministerio Público (FISLAAPD, FECOR, Fiscalía Anticorrupción, Fiscalías Especializadas en Terrorismo, Fiscalía Penales de Lima y fiscalías corporativas en provincias. Se ha informado que al 23 de noviembre de 2018 se han recibido 240 solicitudes de información a través del referido canal, de las cuales 217 corresponden a solicitudes de información nacional (SIN) y 23 a requerimientos de asistencias técnica (ATE), de las cuales han sido atendidas 203 y 15, respectivamente; estando en proceso de atención 14 y 8 respectivamente.  ', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'3.1.1', N'Fortalecer la estructura organizacional y de gestión del capital humano LA/FT de la PNP mediante la especialización de sus efectivos.', N'Se ha desarrollado un curso de capacitación sobre investigación de LA, FT y PD en el marco del Código Procesal Penal (07/05/2018-15/06/2018) y el Curso de Especialización de investigación sobre LA, FT y PD en em marco del Código Procesal Penal - 2018 (04/07/2018-15/10/2018). Y se han programado el desarrollo de dos cursos de capacitación durante los meses de agosto y octubre del 2018.', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'3.1.2', N'Fortalecimiento de las Fiscalías Especializadas de LA/FT a nivel nacional.', N'Se ha programado realizar para el año 2019 cuatro cursos sobre la prueba indiciaria en marco del delito LA, técnicas de litigación oral aplicadas al juicio oral en el marco de los delito de LA/FT,  el proceso de pérdida de dominio, y análisis dogmático y jurispruudencial del delito precendente en el marco del LA.', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'3.1.3', N'Fortalecer la gestión de capital humano del PJ, considerando su especialización en materia LA/FT y mejorando condiciones de seguridad y capacitación.', N'Se ha realizado un curso de capacitación en LA virtual que se llevó a cabo del 20 de agosto al 14 de setiembre de 2018, dirigido a las Cortes Superiores de Justicia de Lima, Lima Este, Lima Norte, Lima Sur, Ventanilla y Callao. Se vienen reallizando las gestiones para realizar un curso-taller sobre LA y PD para los OO.JJ especializados en Delitos de Corrupción de Funcionaarios recientemente creados a partir del 01 de marzo de 2018.', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'3.1.4', N'Aprobar un instrumento conjunto entre el MP y el PJ que permita remitir a los órganos jurisdiccionales los documentos sustentatorios de los requerimientos fiscales en formato digital.', N'Se están realizando gestiones de coordinación entre la Sala Penal Nacional (Juzgafos de Investigación Preparatoria Nacional y Salas Penales de Apelación Nacional) y al Sitema Nacional Anticorrupción (ahora Corte Superior de Justicia Especializada en Delitos de Crimen Organizado y de Corrupción de Funcionarios), la Oficina de Proyectos y Cooperación Internacional, y la Oficina de Convenio del Poder Judicial, para la aelbaoración de un Protocolo de ctuación Interinstitucional, que permita remitir a los órganos jurisdiccionales los documentos sustentatorios de los requimientos de los fiscales en formato digital. Tras una reunión de trabajo, entre el MP y el PJ se acordó enviar mediante discos compactos solo los elementos de convicción que sustentan los requerimientos fiscales.', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'3.1.5', N'Reordenar la estructura organizativa del PJ y del MP para que, en función de la carga procesal soportada, se creen juzgados o fiscalías especializadas en LA y PD, o convertir las existentes para destinarlas a conocer de manera exclusiva el ilícito de LA y PD.', N'"1. Resolución Administrativa. N° 001-2018-CE-PJ que crea y convierte 22 juzgados Anticorrupción en 14 distritos judiciales.   2.  La Coorrdinación Nacional del SEDCF ha elaborado la propuesta de creación de Órganos Jurisdiccionales Especializados en Delitos de Corrupción de Funcionarios a nivel Distrital para el año 2019  (Informe N° 05-2018-CN-SECDF-PJ de Gerencia de Planificación y Presupuesto del Poder Judicial e Informe N° 06-2018-CN-SEDCF-PJ de la Unidad de Equipo Técnico del NCPP-UET). 3. Resolución N° 004-2018-MP-FN-JFS, que dispone la creación de la Fiscalía Provincial Especializada en Delitos de Lavado de Activos y Pérdida de Dominio del Callao con 01 plaza de Fiscal Provincial y 02 plazas de Fiscal Adjunto Provincial. 4. Para el año 2019, se programó crear y convertir 6 fiscalías para conocer casos de Lavados de Activos y Pérdida de Dominio."', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'3.1.6', N'Poner en  vigencia a nivel nacional del Nuevo Código Procesal Penal para los casos de LA/FT.', N'A través del Decreto Supremo N° 015-2017 se modificó el Calendario Oficial de Aplicación Progresiva del Código Procesal Penal y se ha asignado presupuesto para la implementación del CPP en el distrito judicial de Lima Norte a partir del 01 de julio de 2018. Y mediante la Resolución Administrativa N° 153-2018-CE-PJ se establecieron precisiones en la implementación del Código Procesal Penal del Distrito Judicial de Lima Norte .', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'3.2.1', N'Agilizar el proceso de inscripción de las medidas de incautación y decomiso en los registros públicos como resultado de procesos LA/FT, considerando la posibilidad de usar medios tecnológicos.', N'"Se firmó el Convenio Específico N° 007 - Convenio Marco de Cooperación Interinstitucional entre la SUNARP y PJ cuyo objeto es la presentación electrónica de solicitudes de inscripción de medidas cautelares dispuestas por los órganos jurisdiccionales del Poder Judicial. A efectos de agilizar la inscripción de los bienes que han sido objeto de incautación, decomiso o pérdida de dominio, tanto la SUNARP como el PJ han aprobado procedimientos para la presentación electrónica de las medidas de incautación mediante parte judicial con firma digital (Resolución N° 237-2017-SUNARP/SN y Resolución Administrativa N° 296 -2017-CE-PJ). Resolución Administrativa N° 061-2018-CE-PJ que dispone el inicio de actividades para realizar adecuaciones en los sistemas informáticos del Sistema Especializado en Delitos de Corrupción de Funcionarios, a fin de implementar el proyecto “Presentación Electrónica de Medidas Cautelares (incautación). Finalmente, la R.A. N° 140-2018-CE-PJ aprobó, en vía de regularización, dicho proyecto, el mismo que se encuentra operativo desde el 7 de diciembre de 2017 y se implementó como piloto en la Sala Penal Nacional, debiendo continuar su implementación a nivel nacional."', 1, N'Cumplido', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'3.2.2', N'Fortalecer los medios tecnológicos y la estructura organizacional y de gestión de capital humano de la PLAPD y la PEDET, considerando el aumento de la dotación del personal, reduciendo su rotación, incrementando su capacitación.', N'', 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'3.2.3', N'Asignar a la PLAPD la competencia para conocer todos los casos de LA, independientemente del tipo de delito precedente que genere los rendimientos ilícitos, asegurando la dotación de personal y presupuestaria y las condiciones de seguridad necesarias para el cumplimiento de esta función. ', NULL, 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'3.3.1', N'Proponer e impulsar ante el Congreso de la República la modificación del Decreto Legislativo N° 1104 y su desarrollo reglamentario, para optimizar la aplicación de la pérdida de dominio, así como para mejorar la administración y distribución de los activos incautados, decomisados y los declarados en pérdida de dominio. ', N'El 11 de enero de 2018 se ha presentado el Proyecto de Ley N° 2306/2017-CR, que establece normas sobre extinción de dominio para bienes, instrumentos y productos del delito, el cual se encuentra en trámite en la Comisión de Justicia y Derechos Humanos del Congreso de la República. Posteriormente, se aprobó el Decreto Legislativo N° 1373 - Decreto Legislativo sobre Extinción de Dominio, en el que se han establecido mejoras en el proceso de la extinción de dominio como autonomía plena respecto al proceso penal y creación de fiscalías y juzgados especializados. Además, se aprobó el Decreto Supremo N° 007-2019-JUS, que aprueba el Reglamento del Decreto Legislativo N° 1373, D.L. de Extinción de Dominio.', 1, N'Cumplido', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'3.3.2.', N'Aprobación de Decreto Supremo que regule la reparación civil, a fin de asegurar su distribución entre las distintas autoridades competentes.', NULL, 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'3.4.1.', N'Fortalecer la participación del MP en las redes regionales de fiscalías para mejorar la cooperación jurídica internacional en materia LA/FT.', NULL, 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'3.4.2', N'Potenciar mecanismos flexibles de cooperación internacional entre la PNP y sus homólogos de otros países en materia LA/FT.', NULL, 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'3.4.3', N'Elaborar estudios o diagnósticos sobre el fenómeno criminal del LA/FT en el país.', N'Actualmente, se cuenta con una versión preliminar del estudio denominado "Lavado de Activos en el Perú. Análisis de resoluciones judiciales 2012-2018" que contiene información estadísica que describe el fenómeno de lavado de activos y la respuesta judicial que se le brinda. Asimismo, identifica las modalidades de lavado con mayor incidencia, la presencia de organizaciones criminales y su vinculación con determinados fenómenos criminales que pueden constituir delitos precedenes para el lavado de activos, a efectos de brindar una respuesta oportuna desde el Estado.', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'4.1.1.', N'Identificar las comisiones y/o grupos de trabajo intersectoriales cuyas competencias estén relacionadas al ámbito del LA/FT y gestionar la participación en éstas de la Presidencia de la CONTRALAFT o sus miembros.    ', N'Se elaboró el primer informe que identifica y prioriza la participación de la CONTRALAFT en las comsisiones y/o grupos de trabajo intersectoriales cuyas competencias se encuentran relacionadas al ámbito del LA/FT.', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'4.2.1', N'Impulsar la creación de los mecanismos seguros para intercambiar información confiable entre los organismos de prevención, detección e investigación y sanción en materia LA/FT.', NULL, 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'4.2.2', N'Suscribir convenios interinstitucionales entre organismos con funciones de supervisión prudencial de sujetos obligados y los competentes en prevención del LA/FT para el intercambio de información.', NULL, 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'4.2.3', N'Generar espacios de diálogo entre los organismos supervisores LA/FT y las asociaciones gremiales de sujetos obligados, así como potenciar los ya existentes.', NULL, 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'4.2.4', N'Aprobar e implementar  protocolos de actuación conjunta entre el MP y el PJ  para el fortalecimiento del proceso penal de LA/FT.', N'Los 4 protocolos de actuación interinstitucional (i) Protocolo de Allanamiento (pendiente de aprobación), (ii) Protocolo de Levantamiento del secreto bancario, reserva tribuaria y reserva de identidad, (iii) Protocolo de inscripción de bienes incautados, decomisados y declarados en pérdida de dominio y (iv) Protocolo para la aplicación de la incautación han sido elaborados y validados técnicamente por las instituciones involucradas y serán oficiallizados en el corto plazo vía Decreto Supremo. Asimismo, se tiene previsto y garantizada su implementación para el presente año en 7 Distritos Judiciales. Mediante D.S. N° 010-2018-JUS, se aprobó el Protocolo para la aplicación de la incautación, comiso, hallazgo y cadena de custodia.', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'4.2.5', N'Constituir Grupos de Trabajo integrados por UIF-Perú, PNP y MP para la detección e investigación de casos complejos de LA/FT.', NULL, 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'4.3.1', N'Promover la cultura de la prevención LA/FT entre los sujetos obligados y ejecución de acciones de concientización y apoyo.', N'Dos guías están siendo elaboradas conjuntamente entre la UIF y el consultor contratado por la GIZ, se encuntra en la etapa de revisión final. ', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'4.3.2', N'Diseñar y ejecutar campañas de difusión y divulgación pública orientada a la prevención LA/FT.', N'Se ha programado la realización de 5 cursos sobre la lucha contra los delitos de lavado de activos y financiamiento del terrorismo a la luz de la cooperación jurídica internacional en las provincias de Tumbes, Tacna, Cusco, Arequipa e Iquitos. Además, se ha programado un Curso de 4 fechas sobre LA y FT dirigido a la ciudadanía en el mes de octubre que se dictará en los Martes Jurídicos del Centro de Estudios del MINJUS Lima. Finalmente, se está elaborando un video para informar y concientizar a la ciudadanía sobre el problema de LA/FT, viene siendo ejecutado con el apoyo de GIZ.', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'4.3.3', N'Difundir entre la población los canales de denuncia ya implementados a los que puede acudir para poner en conocimiento de las autoridades posibles actos vinculados al LA/FT.', N'Se elaboró el primer informe que identifica y prioriza la participación de la CONTRALAFT en las comsisiones y/o grupos de trabajo intersectoriales cuyas competencias se encuentran relacionadas al ámbito del LA/FT.', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'4.3.4', N'Implementar un mecanismo que permita a la ciudadanía comunicar a la UIF-Perú hechos presuntamente vinculados al LA/FT, guardando la debida confidencialidad.', N'El 27 de noviembre de 2018 se implementó el buzón ciudadano, disponible en el portal SPLAFT de la SBS.', 1, N'Cumplido', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'4.4.1', N'Realizar un estudio para identificar debilidades en las capacidades (formativas y operativas) de los actores involucrados. ', N'Se ha ejecutado un taller para el diseño de la malla formativa los días 14 y 15 de agosto de 2017, con la participación de representantes de diferentes instituciones públicas que componente el SPLAFT y los resultados están recogidos en un informe. ', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'4.4.2', N'Definir medidas articuladas, sobre la base de los resultados del estudio realizado, para mitigar las debilidades identificadas.', NULL, 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'4.4.3', N'Implementar y poner en funcionamiento el Módulo de Información Estadística sobre Lavado de Activos y Financiamiento del Terrorismo en la plataforma del Sistema Integrado de Estadísticas de la Criminalidad y Seguridad Ciudadana del Instituto Nacional de Estadística e Informática.', N'Esta acción viene siendo ejecutada con la asistencia técnica de la Cooperación Alemana (GIZ), y ha sido desarrollado por etapas. Nos encontramos culminando la tercera etapa de diseño informático del Sistema de Información Estadística para todas las instituciones involucradas. Posteriormente, se ejecutará la última etapa del proyecto donde se implementará y desarrollará lo diseñado; para esta última fase se estará contando con el apoyo financiero de la Embajada de los Estados Unidos de Norteamérica.', 1, N'Ejecucion', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'4.5.1.', N'Ejecutar el seguimiento del cumplimiento de los estándares internacionales, como consecuencia de los resultados del Informe de Evaluación Mutua del GAFILAT.', NULL, 1, N'Pendiente', NULL, NULL, NULL, NULL)
INSERT [dbo].[ACCION] ( [N_COD_PLAN], [C_NUM_CODIGO], [C_DES_NOMBRE], [C_DES_DESCRIPCION], [N_FL_ACTIVO], [C_COD_ESTADO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FEC_MODIFICACION]) VALUES ( 1, N'4.5.2', N'Establecer un mecanismo de diálogo entre la SBS y los organismos involucrados para impulsar la implementación de los cambios que se detecten producto del seguimiento del cumplimiento de los estándares internacionales. ', NULL, 1, N'Pendiente', NULL, NULL, NULL, NULL)
*/

