INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES (1, N'La UIF ha elaborado 1 Evaluación Sectorial de la Exposición a los Riesgos de LA/FT del Sistema Financiero. El DSRLAFT, mediante Informe N° 78-2018-DSRLAFT, ha presentado los resultados de la evaluación sectorial de la exposición de riesgos LA/FT de las empresas del Sistema Bancario, Financiero y AFP. Pendiente elaborar durante el 2018: una evaluación en el sector de Juegos de Casino y Máquinas Tragamonedas a cargo del MINCETUR.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES (2, N'Aprobación de la Ley N° 30822, Ley que modifica la Ley N° 26702, Ley General del Sistema Financiero y del Sistema de Seguros y Orgánica de la SBS, y otras normas concordantes, respecto de la regulación y supervisión de las cooperativas de ahorro y crédito. Se dispuso la creación de la Superintendencia Adjunta de Cooperativas encargada de la supervisión de las Coopac de niveles 1 y 2. En materia de prevención de LA, el supervisor en el caso de las nivel 1 es la UIF-Perú, y en las de niveles 2 y 3 estará a cargo de la SBS. Se ha desarrollado un régimen de supervisión, intervención, disolución y liquidación, infracciones y sanciones. Además, se dispuso la creación de un Fondo de Seguro de Depósito Cooperativo y un Registro Nacional de Cooperativas de Ahorro y Crédito no autorizadas a captar recursos.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES (3, N'Se han realizado reuniones de trabajo y coordinaciones entre la SAAJ, UIF-Perú y DAR. Se ha elaborado un único proyecto de norma que comprende la regulación de las actividades de compra y venta de divisas, así como las de préstamos, que se encuentra en proceso de revisión para presentarlo e impulsar su aprobación ante el Congreso de la República. ', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES (4, N'Se han realizado reuniones de trabajo y coordinaciones entre la SAAJ, UIF-Perú y DAR. Se ha elaborado un único proyecto de norma que comprende la regulación de las actividades de compra y venta de divisas, así como las de préstamos, que se encuentra en proceso de revisión para presentarlo e impulsar su aprobación ante el Congreso de la República. ', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES (5, N'Art. 4 de la Resolución SBS N° 789-2018 “Norma para la prevención del lavado de activos y del financiamiento del terrorismo aplicable a los sujetos obligados bajo supervisión de la UIF-Perú, en materia de prevención del lavado de activos y del Financiamiento del Terrorismo” señala lo siguiente: Los sujetos obligados abogados y contadores públicos colegiados deben implementar un sistema de prevención del lavado de activos y financiamiento del terrorismo (SPLAFT). Por ello deben cumplir con: a)   El proceso de debida diligencia en el conocimiento del cliente: deben identificar al cliente en cada operación, sea este persona natural, persona jurídica o ente jurídico, solicitando la información y documentación determinada según se trate del régimen general o reforzado que deben seguir.  b)   Bajo el régimen general de debida diligencia el cliente: los abogados y contadores a que se refiere este artículo deben solicitar a sus clientes la información mínima. c)  Bajo el régimen reforzado, los sujetos obligados deben desarrollar e implementar procedimientos adicionales a los del régimen general, para los clientes que demuestren un patrón de mayor riesgo del LA/FT. Este régimen aplica obligatoriamente a clientes: 1) Nacionales o extranjeros, no residentes. 2) Personas jurídicas no domiciliadas. 3) PEP. d) Para efectos del Registro de Operaciones (RO), los sujetos obligados abogados y contadores deben registrar todas las operaciones individuales siguientes, independientemente del monto de la operación e)   El oficial de cumplimiento de los sujetos obligados abogados y contadores debe enviar a la UIF-Perú Informe Anual del Oficial de Cumplimiento (IAOC) sobre la situación del SPLAFT y su cumplimiento.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES (6, N'Art. 22 del D.S. N° 020-2017-JUS, Reglamento de la Ley N° 27693, Ley que crea la UIF-Perú, dispone que los sujetos obligados deben implementar una política de debida diligencia en el conocimiento de sus trabajadores (que incluye al gerente general, gerentes, administradores) y directores, de contar con dicho órgano de gobierno, la que debe formar parte de su programa o política de reclutamiento y selección del personal, con el fin de garantizar su integridad, para lo cual deben identificarlos y requerirles información, considerando lo establecido en las normas sectoriales. Asimismo, en el artículo 17 de la Resolución SBS N° 789-2018 "Norma para la prevención del LA y FT aplicable a los sujetos obligados bajo supervisión de la UIF-Perú, en materia de prevención del LA y del FT", señala la información mínima que se le debe requerir a los trabajadores y directores. Por otro lado, la propuesta de idoneidad moral de organizadors y accionistas fue incorporada en el nuevo Reglamento de Autorización de Empresas y Representantes de los Sistemas Financieros y de Seguros, así como la modificación del Título II del Compendio de Normas de Superintendencia Reglamentarias del Sistema Privado de AFP, aprobado por la Resolución N° 054-98-EF/SAFP y la modificación del Reglamento de Gobierno Corporativo y Gestión Integral de Riesgos.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES (7, N'Aprobación de la Resolución SBS N° 2755-2018 se aprobó el "Reglamento de infracciones y sanciones de la Superintendencia de Banca, Seguros y Administradoras Privadas de Fondos de Pensiones". ', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES (9, N'Se encuentra en proceso de revisión la actualización del Reglamento de Infracciones y Sanciones aplicable a los sujetos obligados bajo supervisión de la UIF-Perú aprobado por Resolución SBS N° 8930-2012 y sus normas modificatorias, acorde con las nuevas disposiciones establecidas en el TUO e la Ley de Procedimiento Administrativo General. Asimismo, se han realizado coordinaciones con la SUNAT y el MTC para elaborar los proyectos de Reglamentos de Infracciones y Sanciones en materia de prevención LA/FT aplicable a sus supervisados, los cuales se encuentran en proceso de revisión.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES (10, N'Elaboración de un proyecto de ley que propone la modificación del art. 361 de la Ley N° 26702, Ley General del Sistema Financiero y Sistema de Seguros, que establece como tope máximo para las multas 200 UITs. Para ello, se ha dispusto la contratación de una consultoría financiada por GIZ encargada de analizar, entro otros aspéctos, la regulación y los estándares internacionales aplicados por otros supervisores para la determinación de multas, lo cual coadyuvará en la elaboración del proyecto de ley respectivo. ', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 13, N'Aprobación del Decreto Legislativo N° 1372, Decreto Legislativo que regula la obligación de las Personas Jurídicas y/o entes jurídicos de informar la identificación de los beneficiarios finales.   Se establece la obligación de las PPJJ y/o entes jurídicos de informar a través de una DJ, presentada a la SUNAT, la identificación de los beneficiarios finales. Además, otorga a las autoridades competentes acceso a información precisa y actualizada sobre el beneficiario final. Y se aprobó el Decreto Supremo N° 003-2019-EF, que aprueba el Reglamento del Decreto Legislativo N° 1372, que regula la obligación de las personas jurídicas y/o entes jurídicos de informar la identificación de los beneficiarios finales.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 14, N'Se ha presentado el Plan Anual de Visitas del DSRLAFT del año 2018. El Plan Anual de visitas 2019 del DSRLAFT ha sido culminado durante el mes de octubre del 2018, encontrándose actualmente en etapa de consolidación e integración en la SAR.  Por otro lado, UIF-Perú ha elaborado el Plan de supervisión anual en materia de LA/FT con un enfoque basado en riesgos. ', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 15, N'SAR: Lanzamiento de la nueva plataforma del aplicativo del Informe Semestral del Oficial de Cumplimiento (ISOC), se sustivieron 5 reuniones con la totalidad de empresas supervisadas por la SBS para presentar el nuevo aplicativo y recibir sus comentarios antes del lanzamiento.                                                                                                 UIF-Perú: 56 sujetos obligados recibieron retroalimentaciones a cargo de D.S. Publiación del documento normativo interno SBS-PRO-UIF526-01, que incluye los lineamientos de retroalimentación del RO a los sujetos obligados según lo reportado por el DAE: 110 retroalimentaciones en materia RO a diversos sujetos obligados. El DAO realizó 365 retroalimentaciones indivisualizadas a un total de 23 tipos de SO, y se realizaron 91 retoralimentaciones grupales a un total de 30 tipos de SO.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 17, N'Mediante el Informe N° 013-2018-DAE-UIF-SBS, se identificaron los sectores que presentan bajos o nulos niveles de reporte de operaciones sospechosas, así como, aquellos con deficiente calidad de reporte; con el fin de orientar los planes de supervisión y recomendar las medidas necesarias para recibir ROS de calidad.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 18, N'Resolución SBS N° 5085-2018 que aprueba 02 “Retroalimentación de incidencias en información enviada por los sujetos obligados". . Asimismo, se adjuntó el instructivo de llenado y los manuales de usuario. Cabe precisar, que el instructivo, los manales y un tutorial se encuentran publicados en el Portal PLAFT desde mayo 2018. Además, se ha brindado capacitación a 286 SO.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 19, N'Artículo 25 del Decreto Supremo N°020-2017-JUS, sobre el Reporte de Operaciones Sospechosas, establece que los sujetos obligados tienen la obligación de comunicar a la UIF-Perú a través de su oficial de cumplimiento, las operaciones detectadas en el curso de sus actividades, realizadas o que se hayan intentado realizar, que sean consideradas como sospechosas, sin importar los montos involucrados. La comunicación debe ser realizada de forma inmediata y suficiente, es decir, en un plazo que en ningún caso debe exceder las veinticuatro horas (24) desde que la operación es calificada como sospechosa.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 20, N'Se publicó en la página web de la SBS las tipologías LA/FT identificadas durante el presente año, en temas relacionados a modalidades de financiamiento de terrorismo, miembros de una organización sin fines de lucro OSFL que realiza actividades de financiamiento del terrorismo, mal uso de los recursos de una OSFL vinculada a una persona políticamente expuesta y tala ilegal. ', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 21, N'1. Resolución SBS N° 4353-2017, la cual regula la forma y condiciones en que se debe proporcionar a la UIF-Perú la información protegida por el secreto bancario y/o la reserva tributaria. 2. Resolución SBS N° 4589-2017 que establece como comunicaciones de inteligencia financiera nacional, de carácter confidencial y reservado emitidas por la UIF-Perú, al Informe de Inteligencia para Levantamiento de Reservas y a la Nota de Inteligencia Financiera Espontánea.  3. Resolución Administrativa N°090-2017-CE-PJ dispone que los Juzgados de Investigación Preparatoria y Salas de Apelación que conforman la Sala Penal Nacional (LA proveniente de los todos los delitos, excepto corrupción; y, FT) y el Sistema Especializado en Delitos de Corrupción de Funcionarios (LA proveniente de corrupción) conozcan las solicitudes de la UIF sobre levantamiento del secreto bancario y reserva tributaria.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 22, N'El Departamento de Regulación de la SBS está evaluando los procedimientos, condiciones, tipo de información y demás aspectos necesarios que permitan establecer un adecuado y oportuno intercambio de información entre la UIF-Perú y el SINA, JNE y el ONPE, dentro del marco de la Ley N° 27693 y sus normas reglamentarias.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 26, N'Aprobación de la Ley N° 30730, publicada el 21 de febrero de 2018, que establece la bancarización obligatoria de determinadas operaciones pasibles de registros, siempre que superen determinado umbral. La obligatoriedad de utilizar medios de pago para realizar las siguientes operaciones cuando los montos sean iguales o superiores a 3 UIT: i) constitución o transferencia de derechos reales sobre inmuebles, ii) transferencia de propiedad o constitución de derechos reales sobre vehículos nuevos o usados, ya sean aéreos, marítimos o terrestres; y iii) adquisición, aumento y reducción de participación en el capital social de una persona jurídica. Consultoría en proceso de elaboración de propuesta normativa que permita establecer límites al traslado interno de movimiento de efectivo y valores con asistencia técnica de GIZ. Se continúa realizando investigaciones y estudiando la propuesta de esta medida. ', NULL, NULL, NULL, NULL, NULL, NULL)INSERT [dbo].[INDICADOR] ([N_COD_INDICADOR], [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES (20, 27, N'Aprobación de la Resolución SBS N°4705-2017 que incorpora el artículo 50-A en la Resolución SBS N°2660-2015, el mismo que señala lo siguiente: Las empresas deben solicitar información adicional a la señalada en el artículo 50, que permita determinar y sustentar el origen de fondos cuando se efectúen operaciones en moneda extranjera en efectivo, de acuerdo a lo considerado en el artículo 49 del Reglamento, por importes iguales o superiores a: a) Tratándose de transferencias de fondos, el importe es de siete mil quinientos dólares americanos (US$ 7,500.00), su equivalente en otras monedas extranjeras, de ser el caso. b) Cuando se trate de operaciones de compra y/o venta de divisas, el importe es de diez mil dólares americanos (US$ 10,000.00), su equivalente en otras monedas extranjeras, de ser el caso.  c) Para las operaciones no consideradas en los literales precedentes, el importe a considerar es de cincuenta mil dólares americanos (US$ 50,000.00), su equivalente en otras monedas extranjeras, de ser el caso.   Las empresas deben incorporar en el Manual u otro documento normativo interno el listado de información de sustento que solicitarán para este tipo de operaciones en efectivo, así como las medidas que adoptarán cuando el ejecutante de la operación se niegue a proporcionar la información solicitada. Las empresas pueden establecer umbrales menores, de acuerdo a la identificación y evaluación de riesgos que hayan efectuado de acuerdo a lo señalado en el artículo 25 del Reglamento.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 28, N'Durante el 2018, la SUNAT ha reportado que se ha realizado el equipamiento tecnológico (equipos de detector de trazas y explosivos, identificador de químicos, kit anticontrabando, bodyscan, etc.) a nivel nacional. ', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 29, N'Se eleaboró un protocolo de uso del SCES (Sistema de Comunicación con entidades Solicitantes). Se ha implementado el acceso para realizar pedidos a través del SCES al Ministerio Público (FISLAAPD, FECOR, Fiscalía Anticorrupción, Fiscalías Especializadas en Terrorismo, Fiscalía Penales de Lima y fiscalías corporativas en provincias. Se ha informado que al 23 de noviembre de 2018 se han recibido 240 solicitudes de información a través del referido canal, de las cuales 217 corresponden a solicitudes de información nacional (SIN) y 23 a requerimientos de asistencias técnica (ATE), de las cuales han sido atendidas 203 y 15, respectivamente; estando en proceso de atención 14 y 8 respectivamente.  ', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 30, N'Se ha desarrollado un curso de capacitación sobre investigación de LA, FT y PD en el marco del Código Procesal Penal (07/05/2018-15/06/2018) y el Curso de Especialización de investigación sobre LA, FT y PD en em marco del Código Procesal Penal - 2018 (04/07/2018-15/10/2018). Y se han programado el desarrollo de dos cursos de capacitación durante los meses de agosto y octubre del 2018.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 31, N'Se ha programado realizar para el año 2019 cuatro cursos sobre la prueba indiciaria en marco del delito LA, técnicas de litigación oral aplicadas al juicio oral en el marco de los delito de LA/FT,  el proceso de pérdida de dominio, y análisis dogmático y jurispruudencial del delito precendente en el marco del LA.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 32, N'Se ha realizado un curso de capacitación en LA virtual que se llevó a cabo del 20 de agosto al 14 de setiembre de 2018, dirigido a las Cortes Superiores de Justicia de Lima, Lima Este, Lima Norte, Lima Sur, Ventanilla y Callao. Se vienen reallizando las gestiones para realizar un curso-taller sobre LA y PD para los OO.JJ especializados en Delitos de Corrupción de Funcionaarios recientemente creados a partir del 01 de marzo de 2018.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 33, N'Se están realizando gestiones de coordinación entre la Sala Penal Nacional (Juzgafos de Investigación Preparatoria Nacional y Salas Penales de Apelación Nacional) y al Sitema Nacional Anticorrupción (ahora Corte Superior de Justicia Especializada en Delitos de Crimen Organizado y de Corrupción de Funcionarios), la Oficina de Proyectos y Cooperación Internacional, y la Oficina de Convenio del Poder Judicial, para la aelbaoración de un Protocolo de ctuación Interinstitucional, que permita remitir a los órganos jurisdiccionales los documentos sustentatorios de los requimientos de los fiscales en formato digital. Tras una reunión de trabajo, entre el MP y el PJ se acordó enviar mediante discos compactos solo los elementos de convicción que sustentan los requerimientos fiscales.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 34, N'1. Resolución Administrativa. N° 001-2018-CE-PJ que crea y convierte 22 juzgados Anticorrupción en 14 distritos judiciales.   2.  La Coorrdinación Nacional del SEDCF ha elaborado la propuesta de creación de Órganos Jurisdiccionales Especializados en Delitos de Corrupción de Funcionarios a nivel Distrital para el año 2019  (Informe N° 05-2018-CN-SECDF-PJ de Gerencia de Planificación y Presupuesto del Poder Judicial e Informe N° 06-2018-CN-SEDCF-PJ de la Unidad de Equipo Técnico del NCPP-UET). 3. Resolución N° 004-2018-MP-FN-JFS, que dispone la creación de la Fiscalía Provincial Especializada en Delitos de Lavado de Activos y Pérdida de Dominio del Callao con 01 plaza de Fiscal Provincial y 02 plazas de Fiscal Adjunto Provincial. 4. Para el año 2019, se programó crear y convertir 6 fiscalías para conocer casos de Lavados de Activos y Pérdida de Dominio.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 35, N'A través del Decreto Supremo N° 015-2017 se modificó el Calendario Oficial de Aplicación Progresiva del Código Procesal Penal y se ha asignado presupuesto para la implementación del CPP en el distrito judicial de Lima Norte a partir del 01 de julio de 2018. Y mediante la Resolución Administrativa N° 153-2018-CE-PJ se establecieron precisiones en la implementación del Código Procesal Penal del Distrito Judicial de Lima Norte .', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 36, N'Se firmó el Convenio Específico N° 007 - Convenio Marco de Cooperación Interinstitucional entre la SUNARP y PJ cuyo objeto es la presentación electrónica de solicitudes de inscripción de medidas cautelares dispuestas por los órganos jurisdiccionales del Poder Judicial. A efectos de agilizar la inscripción de los bienes que han sido objeto de incautación, decomiso o pérdida de dominio, tanto la SUNARP como el PJ han aprobado procedimientos para la presentación electrónica de las medidas de incautación mediante parte judicial con firma digital (Resolución N° 237-2017-SUNARP/SN y Resolución Administrativa N° 296 -2017-CE-PJ). Resolución Administrativa N° 061-2018-CE-PJ que dispone el inicio de actividades para realizar adecuaciones en los sistemas informáticos del Sistema Especializado en Delitos de Corrupción de Funcionarios, a fin de implementar el proyecto “Presentación Electrónica de Medidas Cautelares (incautación). Finalmente, la R.A. N° 140-2018-CE-PJ aprobó, en vía de regularización, dicho proyecto, el mismo que se encuentra operativo desde el 7 de diciembre de 2017 y se implementó como piloto en la Sala Penal Nacional, debiendo continuar su implementación a nivel nacional.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 39, N'El 11 de enero de 2018 se ha presentado el Proyecto de Ley N° 2306/2017-CR, que establece normas sobre extinción de dominio para bienes, instrumentos y productos del delito, el cual se encuentra en trámite en la Comisión de Justicia y Derechos Humanos del Congreso de la República. Posteriormente, se aprobó el Decreto Legislativo N° 1373 - Decreto Legislativo sobre Extinción de Dominio, en el que se han establecido mejoras en el proceso de la extinción de dominio como autonomía plena respecto al proceso penal y creación de fiscalías y juzgados especializados. Además, se aprobó el Decreto Supremo N° 007-2019-JUS, que aprueba el Reglamento del Decreto Legislativo N° 1373, D.L. de Extinción de Dominio.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 43, N'Actualmente, se cuenta con una versión preliminar del estudio denominado "Lavado de Activos en el Perú. Análisis de resoluciones judiciales 2012-2018" que contiene información estadísica que describe el fenómeno de lavado de activos y la respuesta judicial que se le brinda. Asimismo, identifica las modalidades de lavado con mayor incidencia, la presencia de organizaciones criminales y su vinculación con determinados fenómenos criminales que pueden constituir delitos precedenes para el lavado de activos, a efectos de brindar una respuesta oportuna desde el Estado.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 44, N'Se elaboró el primer informe que identifica y prioriza la participación de la CONTRALAFT en las comsisiones y/o grupos de trabajo intersectoriales cuyas competencias se encuentran relacionadas al ámbito del LA/FT.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 48, N'Los 4 protocolos de actuación interinstitucional (i) Protocolo de Allanamiento (pendiente de aprobación), (ii) Protocolo de Levantamiento del secreto bancario, reserva tribuaria y reserva de identidad, (iii) Protocolo de inscripción de bienes incautados, decomisados y declarados en pérdida de dominio y (iv) Protocolo para la aplicación de la incautación han sido elaborados y validados técnicamente por las instituciones involucradas y serán oficiallizados en el corto plazo vía Decreto Supremo. Asimismo, se tiene previsto y garantizada su implementación para el presente año en 7 Distritos Judiciales. Mediante D.S. N° 010-2018-JUS, se aprobó el Protocolo para la aplicación de la incautación, comiso, hallazgo y cadena de custodia.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 50, N'Dos guías están siendo elaboradas conjuntamente entre la UIF y el consultor contratado por la GIZ, se encuntra en la etapa de revisión final. ', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 51, N'Se ha programado la realización de 5 cursos sobre la lucha contra los delitos de lavado de activos y financiamiento del terrorismo a la luz de la cooperación jurídica internacional en las provincias de Tumbes, Tacna, Cusco, Arequipa e Iquitos. Además, se ha programado un Curso de 4 fechas sobre LA y FT dirigido a la ciudadanía en el mes de octubre que se dictará en los Martes Jurídicos del Centro de Estudios del MINJUS Lima. Finalmente, se está elaborando un video para informar y concientizar a la ciudadanía sobre el problema de LA/FT, viene siendo ejecutado con el apoyo de GIZ.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 52, N'Se elaboró el primer informe que identifica y prioriza la participación de la CONTRALAFT en las comsisiones y/o grupos de trabajo intersectoriales cuyas competencias se encuentran relacionadas al ámbito del LA/FT.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 53, N'El 27 de noviembre de 2018 se implementó el buzón ciudadano, disponible en el portal SPLAFT de la SBS.', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 54, N'Se ha ejecutado un taller para el diseño de la malla formativa los días 14 y 15 de agosto de 2017, con la participación de representantes de diferentes instituciones públicas que componente el SPLAFT y los resultados están recogidos en un informe. ', NULL, NULL, NULL, NULL, NULL, NULL)
INSERT [dbo].[INDICADOR] ( [N_COD_ACCION], [C_DES_DETALLE], [N_COD_ESTADO], [N_FL_ACTIVO], [C_USU_REGISTRO], [D_FEC_REGISTRO], [C_USU_MODIFICACION], [D_FECHA_MODIFICACION]) VALUES ( 56, N'Esta acción viene siendo ejecutada con la asistencia técnica de la Cooperación Alemana (GIZ), y ha sido desarrollado por etapas. Nos encontramos culminando la tercera etapa de diseño informático del Sistema de Información Estadística para todas las instituciones involucradas. Posteriormente, se ejecutará la última etapa del proyecto donde se implementará y desarrollará lo diseñado; para esta última fase se estará contando con el apoyo financiero de la Embajada de los Estados Unidos de Norteamérica.', NULL, NULL, NULL, NULL, NULL, NULL)
*/


